VideoSite.script:
function canHandleURL(URL) {
  if (URL.toLowerCase().substr(0, 29) == "http://www.clipfish.de/video/") return true;
  else								      return false;
}

function getVideoURL(html) {
  var devxml = downloadURL("http://www.clipfish.de" + (html.match(/data: "(.*?)"/), RegExp.$1));

  return devxml.match(/<filename>(.*?)<\/filename>/), RegExp.$1;
}

function queryMetadata(html) {
  var metadata = new Metadata();

  var devxml = downloadURL("http://www.clipfish.de" + (html.match(/data: "(.*?)"/), RegExp.$1));

  metadata.title	   = (devxml.match(/<title><!\[CDATA\[(.*?)\]\]><\/title>/), RegExp.$1);
  metadata.description = (html.match(/Beschreibung: <\/label> <span .*?>(.*?)<\/span>/), RegExp.$1);

  metadata.date	   = (html.match(/(?:Upload:|Jahr:) <\/label>(.*?)<br \/>/), RegExp.$1);
  metadata.thumbnail   = (devxml.match(/<imageurl>(.*?)<\/imageurl>/), RegExp.$1);

  metadata.uploader	   = (html.match(/von: <\/span><a .*?>(.*?)<\/a>/), RegExp.$1);

  return metadata;
}
