VideoSite.script:
function canHandleURL(URL) {
  if (URL.toLowerCase().substr(0, 33) == "http://www.dailymotion.com/video/") return true;
  else									  return false;
}

function getVideoURL(html) {
  var sequence = unescape((html.match(/so.addVariable\("sequence",  "(.*?)"\);/), RegExp.$1));

  return (sequence.match(/"hqURL":"(.*?)"/), RegExp.$1).replace(/\\\//g, "/");
}

function queryMetadata(html) {
  var metadata = new Metadata();

  metadata.title	   = (html.match(/<Attribute name="title">(.*?)<\/Attribute>/), RegExp.$1);
  metadata.description = (html.match(/<div .*? id="video_description">(.*?)<\/div>/), RegExp.$1);

  metadata.date	   = (html.match(/<Attribute name="date">(.*?)<\/Attribute>/), RegExp.$1);
  metadata.thumbnail   = (html.match(/<link rel="thumbnail" type="image\/jpeg" href="(.*?)" \/>/), "http://www.dailymotion.com" + RegExp.$1);

  metadata.uploader	   = (html.match(/<Attribute name="owner">(.*?)<\/Attribute>/), RegExp.$1);

  return metadata;
}
