VideoSite.script:
function canHandleURL(URL) {
  if (URL.toLowerCase().search(/^http:\/\/(.*?\.)?video\.yahoo\.com\/watch\//) != -1) return true;
  else										  return false;
}

function getVideoURL(html) {
  var playlist = downloadURL("http://cosmos.bcst.yahoo.com/up/yep/process/getPlaylistFOP.php?node_id=" + (html.match(/so.addVariable\("id", "(.*?)"\);/), RegExp.$1));

  return playlist.match(/<STREAM APP="(.*?)" FULLPATH="(.*?)" .*?><\/STREAM>/), (RegExp.$1 + RegExp.$2).replace(/&amp;/g, "&");
}

function queryMetadata(html) {
  var metadata = new Metadata();

  metadata.title	 = (html.match(/<meta name="title" content="(.*?)" \/>/), RegExp.$1);

  metadata.thumbnail = (html.match(/so.addVariable\("thumbUrl", "(.*?)"\);/), RegExp.$1);

  metadata.uploader	 = (html.match(/<h2 class="ti-5"><a .*?>(.*?)<\/a><\/h2>/), RegExp.$1);

  return metadata;
}
