VideoSite.script:
function canHandleURL(URL) {
  if (URL.toLowerCase().match(/http:\/\/www.youtube.com\/watch\?v=.*/)) return true;
  else								    return false;
}

function getVideoURL(html) {
  return html.match(/"fmt_stream_map": "[0-9]+\|(.*?)\|/), (RegExp.$1).replace(/\\\//g, "/");
}

function queryMetadata(html) {
  var metadata = new Metadata();

  metadata.title	   = (html.match(/<meta name="title" content="(.*?)">/) ? RegExp.$1 : null);
  metadata.description = (html.match(/<p id="eow-description"\s*>(.*?)<\/p>/) ? RegExp.$1 : null);

  metadata.date	   = (html.match(/<span id="eow-date" class="watch-video-date"\s*>\s*(.*?)\s*<\/span>/) ? RegExp.$1 : null);
  metadata.thumbnail   = (html.match(/<meta property="og:image" content="(.*?)" \/>/) ? RegExp.$1 : null);

  metadata.uploader    = (html.match(/'VIDEO_USERNAME': '(.*?)'/) ? RegExp.$1 : null);

  return metadata;
}
